/* jshint browser: true */
/* global $ */

module.exports = function(core, config, store) {
	var desktopnotify = require("../ui/desktopnotify.js"),
		browserNotify = (function() {
			var hasFocus = false,
				notifyStatus = false,
				newTitle,
				originalTitle,
				titleTimer,
				soundTimer,
				play = (function() {
					var $el,
						sound = "data:audio/wav;base64,";

					return function() {
						if (!$el) {
							$el = $("<div>").attr("id", "notify-sound")
								.css({
									display: "none"
								})
								.appendTo("body");
						}

						$el.append('<audio autoplay><source src="' + sound + '" type="audio/wav">' +
							'<embed hidden="true" autostart="true" loop="false" src="' + sound + '"></audio>');
					};
				}());

			$(window).on("focus", function() {
				if (originalTitle) {
					document.title = originalTitle;
					originalTitle = null;
				}

				if (titleTimer) {
					clearInterval(titleTimer);
				}

				hasFocus = true;
				notifyStatus = false;
			}).on("blur", function() {
				hasFocus = false;
			});

			hasFocus = document.hasFocus();

			return function(text, important, sound) {
				if (hasFocus) {
					return;
				}

				if (!originalTitle) {
					originalTitle = document.title;
				}

				newTitle = text;

				if (!notifyStatus) {
					clearInterval(titleTimer);

					titleTimer = setInterval(function() {
						document.title = (document.title === originalTitle) ? newTitle : originalTitle;
						notifyStatus = true;
					}, 1000);
				}

				if (important && soundTimer) {
					clearTimeout(soundTimer);
					soundTimer = null;
				}

				if (sound && !soundTimer) {
					play();

					soundTimer = setTimeout(function() {
						soundTimer = null;
					}, 30000);
				}
			};
		}());

	core.on('text-dn', function(text, next) {
		var user = store.getUser(),
			sound = (user && user.params && user.params.notifications && typeof user.params.notifications.sound === "boolean") ? user.params.notifications.sound : true;

		if (user && text.mentions && text.mentions.indexOf(user.id) > -1) {
			browserNotify(text.text, true, sound);

			if (store.getUser().params.notifications.desktop) {
				desktopnotify.show({
					title: "New mention on " + text.to,
					body: (text.from ? text.from.replace(/^guest-/, "") + ": " : "") + text.text,
					icon: "/s/img/scrollback-dark.png",
					tag: text.id,
					action: function() {
						core.emit("setstate", {
							nav: {
								room: text.to,
								time: text.time
							}
						});
					}
				});
			}
		} else {
			browserNotify(text.text, false, sound);
		}

		next();
	}, 100);
};
